import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { AsyncStorage } from "react-native";

const Stack = createStackNavigator();

import { LoginScreen } from "./components/LoginScreen";
import { SignInScreen } from "./components/SignInScreen";
import { HomeScreen } from "./components/HomeScreen";

export default function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [logged, setLogged] = useState(false);

  async function checkUserAlreadyLogged() {
    try {
      const userAlreadyLogged = await AsyncStorage.getItem("logged");
      if (!!userAlreadyLogged) {
        setLogged(true);
      }
      setIsLoading(false);
    } catch (e) {
      console.log(e);
    }
  }

  useEffect(() => {
    checkUserAlreadyLogged();
    console.log(logged);
  });

  if (logged) {
    return (
      <NavigationContainer>
        <Stack.Navigator
          screenOptions={{
            headerStyle: {
              backgroundColor: "#1d1d1d"
            },
            headerTintColor: "#fff",
            headerTitleStyle: {},
            headerTitleAlign: "center"
          }}></Stack.Navigator>
      </NavigationContainer>
    );
  } else {
    return (
      <NavigationContainer>
        <Stack.Navigator
          screenOptions={{
            headerStyle: {
              backgroundColor: "#1d1d1d"
            },
            headerTintColor: "#fff",
            headerTitleStyle: {},
            headerTitleAlign: "center"
          }}></Stack.Navigator>
      </NavigationContainer>
    );
  }
}

function SwitchNavigation({ isLoading, logged }) {
  if (isLoading) {
    return (
      <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        <Text>Ecran de chargement</Text>
      </View>
    );
  } else if (logged) {
    return (
      <Stack.Screen name="Home">
        {props => <HomeScreen {...props} setLogged={setLogged} />}
      </Stack.Screen>
    );
  } else {
    return (
      <Stack.Screen name="Login">
        {props => <LoginScreen {...props} setLogged={setLogged} />}
      </Stack.Screen>
    );
  }
}
